DROP TABLE IF EXISTS scriptures;
CREATE TABLE scriptures (
  scId int(11) NOT NULL AUTO_INCREMENT,
  scOT tinyint(1) NOT NULL DEFAULT '0',
  scBook varchar(32) DEFAULT NULL,
  scVerse int(11) NOT NULL DEFAULT '0',
  scText text,
  PRIMARY KEY (scId),
  KEY books (scBook,scVerse)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

